'use strict';

const {ChildProcess, spawn} = require('child_process');
const {createServer} = require('net');
const fetch = require('node-fetch');
const {join, resolve} = require('path');

/**
 * Removes comments and whitespace from a PHP script, by calling a Web service.
 * @implements {Transformer}
 */
class FastTransformer {

  /**
   * The default address that the server is listening on.
   * @type {string}
   */
  static get defaultAddress() {
    return '127.0.0.1';
  }

  /**
   * Initializes a new instance of the class.
   * @param {string} [executable] The path to the PHP executable.
   */
  constructor(executable = 'php') {

    /**
     * The path to the PHP executable.
     * @type {string}
     */
    this._executable = executable;

    /**
     * The port that the PHP process is listening on.
     * @type {number}
     */
    this._port = -1;

    /**
     * The underlying PHP process.
     * @type {ChildProcess}
     */
    this._process = null;
  }

  /**
   * The class name.
   * @type {string}
   */
  get [Symbol.toStringTag]() {
    return 'FastTransformer';
  }

  /**
   * Value indicating whether the PHP process is currently listening.
   * @type {boolean}
   */
  get listening() {
    return this._process instanceof ChildProcess;
  }

  /**
   * Terminates the underlying PHP process: stops the server from accepting new connections. It does nothing if the server is already closed.
   */
  async close() {
    if (!this.listening) return;
    this._process.kill();
    this._process = null;
  }

  /**
   * Starts the underlying PHP process: begins accepting connections. It does nothing if the server is already started.
   * @return {Promise<number>} The port used by the PHP process.
   */
  async listen() {
    if (this.listening) return this._port;

    this._port = await this._getPort();
    return new Promise((resolve, reject) => {
      this._process = spawn(this._executable, ['-S', `${FastTransformer.defaultAddress}:${this._port}`, '-t', join(__dirname, 'php')]);
      this._process.on('error', err => reject(err));
      setTimeout(() => resolve(this._port), 1000);
    });
  }

  /**
   * Processes a PHP script.
   * @param {string} script The path to the PHP script.
   * @return {Promise<string>} The transformed script.
   */
  async transform(script) {
    let port = await this.listen();
    let endPoint = new URL(`http://${FastTransformer.defaultAddress}:${port}/server.php`);
    endPoint.searchParams.set('file', resolve(script));

    let res = await fetch(endPoint.href);
    if (!res.ok) throw new Error('An error occurred while transforming the script.');
    return res.text();
  }

  /**
   * Gets an ephemeral port chosen by the system.
   * @return {Promise<number>} A port that the server can listen on.
   */
  async _getPort() {
    return new Promise((resolve, reject) => {
      let server = createServer().unref();
      server.on('error', err => reject(err));
      server.listen(0, FastTransformer.defaultAddress, () => {
        let port = server.address().port;
        server.close(() => resolve(port));
      });
    });
  }
}

// Module exports.
exports.FastTransformer = FastTransformer;

'use strict';

const {execFile} = require('child_process');
const {resolve} = require('path');
const {promisify} = require('util');

/**
 * Removes comments and whitespace from a PHP script, by calling a PHP process.
 * @implements {Transformer}
 */
class SafeTransformer {

  /**
   * Initializes a new instance of the class.
   * @param {string} [executable] The path to the PHP executable.
   */
  constructor(executable = 'php') {

    /**
     * The path to the PHP executable.
     * @type {string}
     */
    this._executable = executable;
  }

  /**
   * The class name.
   * @type {string}
   */
  get [Symbol.toStringTag]() {
    return 'SafeTransformer';
  }

  /**
   * Closes this transformer and releases any resources associated with it.
   * @return {Promise} Completes when the transformer is finally disposed.
  */
  async close() {
    return null;
  }

  /**
   * Processes a PHP script.
   * @param {string} script The path to the PHP script.
   * @return {Promise<string>} The transformed script.
   */
  async transform(script) {
    const exec = promisify(execFile);
    let {stdout} = await exec(this._executable, ['-w', resolve(script)], {maxBuffer: 10 * 1024 * 1024});
    return stdout;
  }
}

// Module exports.
exports.SafeTransformer = SafeTransformer;

'use strict';
const {Enum} = require('@cedx/enum');

/**
 * Defines the type of transformation applied by a minifier.
 * @type {Object}
 *
 * @property {string} fast Applies a fast transformation.
 * @property {string} safe Applies a safe transformation.
 */
const TransformMode = Enum.create({
  fast: 'fast',
  safe: 'safe'
});

// Module exports.
exports.TransformMode = TransformMode;

'use strict';

const {FastTransformer} = require('./fast_transformer.js');
const {TransformMode} = require('./transform_mode.js');
const {SafeTransformer} = require('./safe_transformer.js');

/**
 * Interface providing a mechanism for transforming input and producing output.
 * @interface
 */
class Transformer {

  /**
   * Creates a new transformer.
   * @param {string} mode The transformation type.
   * @param {string} [executable] The path to the PHP executable.
   * @return {Transformer} The newly created instance.
   */
  static factory(mode, executable = 'php') {
    return mode == TransformMode.fast ? new FastTransformer(executable) : new SafeTransformer(executable);
  }

  /**
   * Initializes a new instance of the class.
   * @throws {TypeError} This class is not instantiable.
   */
  constructor() {
    throw new TypeError('This type is not instantiable.');
  }

  /* eslint-disable no-unused-vars, valid-jsdoc */

  /**
   * Closes this transformer and releases any resources associated with it.
   * @return {Promise} Completes when the transformer is finally disposed.
   */
  async close() {
    throw new Error('Not implemented.');
  }

  /**
   * Processes a PHP script.
   * @param {string} script The path to the PHP script.
   * @return {Promise<string>} The transformed script.
   */
  async transform(script) {
    throw new Error('Not implemented.');
  }

  /* eslint-enable no-unused-vars, valid-jsdoc */
}

// Module exports.
exports.Transformer = Transformer;

'use strict';

const {expect} = require('chai');
const {FastTransformer} = require('../lib/index.js');

/**
 * @test {FastTransformer}
 */
describe('FastTransformer', function() {
  this.retries(2);
  this.timeout(30000);

  /**
   * @test {FastTransformer#close}
   */
  describe('#close()', () => {
    let transformer = new FastTransformer;

    it('should complete without any error', async () => {
      await transformer.close();
      expect(true).to.be.ok;
    });

    it('should be callable multiple times', async () => {
      await transformer.close();
      await transformer.close();
      expect(true).to.be.ok;
    });
  });

  /**
   * @test {FastTransformer#listen}
   */
  describe('#listen()', () => {
    let transformer = new FastTransformer;
    after(async () => await transformer.close());

    it('should complete without any error', async () => {
      await transformer.listen();
      expect(true).to.be.ok;
    });

    it('should be callable multiple times', async () => {
      await transformer.listen();
      await transformer.listen();
      expect(true).to.be.ok;
    });
  });

  /**
   * @test {FastTransformer#listening}
   */
  describe('#listening', () => {
    let transformer = new FastTransformer;

    it('should return whether the server is listening', async () => {
      expect(transformer.listening).to.be.false;

      await transformer.listen();
      expect(transformer.listening).to.be.true;

      await transformer.close();
      expect(transformer.listening).to.be.false;
    });
  });

  /**
   * @test {FastTransformer#transform}
   */
  describe('#transform()', () => {
    let script = 'test/fixtures/sample.php';
    let transformer = new FastTransformer;
    after(async () => await transformer.close());

    it('should remove the inline comments', async () => {
      expect(await transformer.transform(script)).to.contain("<?= 'Hello World!' ?>");
    });

    it('should remove the multi-line comments', async () => {
      expect(await transformer.transform(script)).to.contain('namespace dummy; class Dummy');
    });

    it('should remove the single-line comments', async () => {
      expect(await transformer.transform(script)).to.contain('$className = get_class($this); return $className;');
    });

    it('should remove the whitespace', async () => {
      expect(await transformer.transform(script)).to.contain('__construct() { }');
    });
  });
});

'use strict';

const {expect} = require('chai');
const File = require('vinyl');
const {Minifier, TransformMode} = require('../lib/index.js');

/**
 * @test {Minifier}
 */
describe('Minifier', function() {
  this.retries(3);
  this.timeout(60000);

  /**
   * @test {Minifier.factory}
   */
  describe('.factory()', () => {
    it('should return a `Minifier` instance', () => {
      expect(Minifier.factory()).to.be.instanceof(Minifier);
    });

    it('should properly initialize the instance properties', () => {
      let minifier = Minifier.factory({
        binary: '/usr/local/bin/php',
        mode: TransformMode.fast,
        silent: true
      });

      let executable = process.platform == 'win32' ? '\\usr\\local\\bin\\php' : '/usr/local/bin/php';
      expect(minifier.silent).to.be.true;
      expect(minifier._transformer).to.equal(`fast:${executable}`);
    });
  });

  /**
   * @test {Minifier#_transform}
   */
  describe('#_transform()', () => {
    let minifier = new Minifier({silent: true});
    after(() => minifier.emit('end'));

    it('should remove the comments and whitespace', async () => {
      let file = new File({path: 'test/fixtures/sample.php'});
      await minifier._transform(file);
      expect(file.contents.toString()).to.contain("<?= 'Hello World!' ?>")
        .and.contain('namespace dummy; class Dummy')
        .and.contain('$className = get_class($this); return $className;')
        .and.contain('__construct() { }');
    });
  });
});

